import { Component, OnInit } from '@angular/core';
import { FormGroup, FormBuilder } from '@angular/forms';
import { Router } from '@angular/router';

import { Employee } from 'src/model/employee';

@Component({
  selector: 'app-user-management',
  templateUrl: './user-management.component.html',
  styleUrls: ['./user-management.component.css']
})
export class UserManagementComponent implements OnInit {

  totalEmployeePerPage: number[] = [5, 10, 15, 20];

  items: Employee[]; // Dữ liệu cần phân trang
  displayedItems: Employee[] = []; // Dữ liệu hiển thị trên trang hiện tại
  itemsPerPage: number = 5; // Số lượng mục trên mỗi trang
  currentPage: number = 1; // Trang hiện tại
  totalPages: number; // Tổng số trang

  listForm: FormGroup;
  constructor(private fb: FormBuilder, private router: Router) {
    // Khởi tạo dữ liệu và gán cho biến items
    this.items = this.getListUser(20);
    this.totalPages = Math.ceil(this.items.length / this.itemsPerPage);
    this.updateDisplayedItems();
    this.listForm = this.fb.group({
    })
  }

  ngOnInit() {
  }

  passUpdate(item: Employee){
    const status = "update";
    console.log("item = " + item);
    this.router.navigate(["/userInfo"], {queryParams: {item, status}});
  }
  updateItemPerPage(target: any){
    this.itemsPerPage = target.value;
    this.updateDisplayedItems();
  }

  onPageChange(page: number) {
    this.currentPage = page;
    this.totalPages = Math.ceil(this.items.length / this.itemsPerPage);
    this.updateDisplayedItems();
  }

  updateDisplayedItems() {
    const startIndex = (this.currentPage - 1) * this.itemsPerPage;
    const endIndex = startIndex + this.itemsPerPage;
    this.displayedItems = this.items.slice(startIndex, endIndex);
  }

  fullname(): any {
    const firstName = ["Trung", "Thanh", "Quoc", "Anh", "Thi Tam", "Thi An", "Quynh Anh", "Van Toan", "Huy"];
    const lastName = ["Tran", "Nguyen", "Ly", "Phan", "Le", "Hoang", "Vu", "Đinh", "Đang", "Duong"];
    const fIndex = Math.floor(Math.random() * firstName.length);
    const lIndex = Math.floor(Math.random() * lastName.length);
    return firstName[fIndex] + " " + lastName[lIndex];
  }


  birthay(): any {
    const dd = Math.floor(Math.random()) + 1;
    const MM= Math.floor(Math.random() * 31) + 1;
    const YY = Math.floor(Math.random() * 15) +  1990;
    return YY + "-" + MM + "-" + dd;
  }

  gender(): any {
    const g = [true, false];
    return g[Math.floor(Math.random()*2)];
  }

  email(): any {

  }

  phone(): any {
    var number = "0";
    for(var i = 0; i < 9; i++){
      number += (Math.floor(Math.random() * 9) +1 ) + "";
    }
    return number;
  }

  address(): any {
    var add = ["HaNoi", "DaNang", "HoChiMinh"];
    return add[Math.floor(Math.random() * add.length)];
  }

  department(): any {
    const de = ["IT", "Marketing", "Sale"];
    return Math.floor(Math.random() * de.length);
  }

  block(): any {
    const g = [true, false];
    return g[Math.floor(Math.random()*2)];
  }

  role(): any{
    return 1;
  }

  getListUser(total: number): Employee[] {
      let list: Employee[] =[];
      for(var i = 0; i < total; i++){
        let emp: Employee = {
          EmpID: i+1,
          Fullname: this.fullname(),
          Birthday: this.birthay(),
          Gender: this.gender(),
          Email: this.fullname().replace(/\s/g, "")+ i+1 + "@gmail.com",
          Phone: this.phone(),
          Address: this.address(),
          DepartmentID: 1,
          Block: this.block(),
          RoleID: 1
        }
        list.push(emp);
      }
      return list;
  }
}
<app-header></app-header>
<div class="card">
  <!-- card header -->
  <div class="card-header d-flex justify-content-between">
    <div class="fs-3 fw-bold">Employee Management</div>
    <div>
      <a href="" class="d-flex justify-content-center btn btn-primary">
        <i class="fa-solid fa-user-plus mt-1"></i>
        <div class="mx-2">Add New</div>
      </a>
    </div>
  </div>
  <!-- card body -->
  <div class="card-body">
    <form [formGroup]="listForm">
    <div class="d-flex justify-content-between">
      <!-- Choose total user are displayed in page -->
      <div class="d-flex">
        <div class="mx-2 mt-1">Show</div>
        <select class="form-select align-items-center" id="totalUserOption" (change)="updateItemPerPage($event.target)">
          <option *ngFor="let number of totalEmployeePerPage" [ngValue]="number">{{number}}</option>
        </select>
        <div class="mt-1">entries</div>
      </div>

      <div class="d-flex">
        <!-- Select user status will be display -->
        <div class="d-flex mx-3">
          <div class="mt-1 mx-2">Status</div>
          <select class="form-select">
            <option value="-1">All</option>
            <option value="false">Unblock</option>
            <option value="true">Block</option>
          </select>
        </div>
        <!-- Input name for search -->
        <div class="form-group d-flex">
          <label for="" class="mt-1 mx-2">Name</label>
          <input type="text" class="form-control">
          <button class="btn btn-primary mx-2"><i class="fa-solid fa-magnifying-glass"></i></button>
        </div>
      </div>
    </div>

    <table class="table table-bordered mt-3">
      <thead>
        <tr>
          <th>No.</th>
          <th>Fullname</th>
          <th>Gender</th>
          <th>Email</th>
          <th>Phone</th>
          <th>Action</th>
        </tr>
      </thead>
      <tbody>
        <tr *ngFor="let item of displayedItems; let i = index">
          <td>{{i+1}}</td>
          <td>{{item.Fullname}}</td>
          <td>
            <div *ngIf="item.Gender == true">
              Male
            </div>
            <div *ngIf="item.Gender == false">
              Female
            </div>
          </td>
          <td>{{item.Email}}</td>
          <td>{{item.Phone}}</td>
          <td>
            <div class="d-flex">
              <button class="btn btn-warning" (click)="passUpdate(item)">Update</button>
              <div>
                <div *ngIf="item.Block == true" class="btn btn-primary mx-2 block">UnBlock</div>
                <div *ngIf="item.Block == false" class="btn btn-danger mx-2 block px-4">Block</div>
              </div>
            </div>
          </td>
        </tr>
      </tbody>
    </table>

    <div class="d-flex justify-content-between">
      <div>You are at {{currentPage}}/{{totalPages}} pages</div>
      <nav aria-label="...">
        <ul class="pagination">
          <li class="page-item disabled">
            <a class="page-link" href="#" tabindex="-1">Previous</a>
          </li>
          <li class="page-item"><a class="page-link" href="#" >1</a></li>
          <li class="page-item active">
            <a class="page-link" href="#">2 <span class="sr-only">(current)</span></a>
          </li>
          <li class="page-item" [ngClass]="{'d-none': totalPages < 3}"><a class="page-link" href="#">3</a></li>
          <li class="page-item">
            <a class="page-link" href="#">Next</a>
          </li>
        </ul>
      </nav>
    </div>
  </form>
  </div>
</div>
