import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormControl, FormGroup, Validators } from '@angular/forms';
import { ActivatedRoute } from '@angular/router';
import { Employee } from 'src/model/employee';

@Component({
  selector: 'app-user-information',
  templateUrl: './user-information.component.html',
  styleUrls: ['./user-information.component.css']
})
export class UserInformationComponent implements OnInit {

  user: Employee = {
    EmpID: 0,
    Fullname: '',
    Birthday: '',
    Gender: true,
    Email: '',
    Phone: '',
    Address: '',
    DepartmentID: 0,
    Block: true,
    RoleID: 0
  };
  status: string = '';
  userData: FormGroup;
  fullname: FormControl = new FormControl('');
  constructor(private fb: FormBuilder, private route: ActivatedRoute) {
    this.route.queryParams.subscribe(params => {
      // if(params['queryData']) this.user = JSON.parse(params['Employee']);
      this.status = params['status'];
      this.fullname = new FormControl(this.user.Fullname);
      console.log('status = ' + this.status);
      console.log('user = ' + this.user);
      console.log('test = ' + params['queryData']);
      console.log('fullname = ' + this.fullname.value);
    })
    this.userData = this.fb.group({
      // fullname: [this.user.Fullname]
    })
  }

  ngOnInit() {
    // this.route.queryParams.subscribe(params => {
    //   this.user = params['item'];
    //   this.status = params['status'];
    //   console.log('status = ' + this.status);
    //   console.log('user = ' + this.user);
    // })
  }

  getUserID(userID: number){

  }
}
<div class="container-fluid d-flex justify-content-center bg-light">
  <div class="col-7 card mt-4" id="user-info-form">
    <div class="fs-3 mx-4">User information</div>
    <div class="d-flex justify-content-between">
      <div class="col-2">
        <div class="w-100 mx-4">
          <img src="../../assets/images/user-icon.png" alt="" class="w-100 mt-3">
        </div>
        <div class="text-center mx-4">User name</div>
      </div>
      <div class="col-8">
        <form>
          <div class="form-group mx-4">
            <label for="">Fullname</label>
            <input type="text" class="form-control" [formControl]="fullname">
          </div>
          <div class="d-flex mx-4 justify-content-between mt-3">
            <div class="col-6 form-group">
              <label for="">Birthday</label>
              <input type="date" class="form-control">
            </div>
            <div class="col-5 form-group">
              <label for="">Gender</label>
              <select name="" id="" class="form-select">
                <option value="1">Male</option>
                <option value="0">Female</option>
              </select>
            </div>
          </div>
          <div class="form-group mx-4 mt-3">
            <label for="">Email</label>
            <input type="email" class="form-control">
          </div>
          <div class="form-group mx-4 mt-3">
            <label for="">Phone</label>
            <input type="text" class="form-control">
          </div>
          <div class="form-group mx-4 mt-3">
            <label for="">Address</label>
            <textarea class="form-control"></textarea>
          </div>
          <div class="form-group mx-4 mt-3">
            <label for="">Department</label>
            <select name="" id="" class="form-select">
              <option value="">IT</option>
            </select>
          </div>
          <div class="form-group mx-4 mt-3">
            <label for="">User role</label>
            <select name="" id="" class="form-select">
              <option value="">User</option>
            </select>
          </div>
          <div class="d-flex justify-content-center mt-4 mb-3">
            <button class="btn btn-warning mx-3">CLEAR</button>
            <input type="submit" class="btn btn-primary" value="UPDATE">
          </div>
        </form>
      </div>
    </div>
  </div>
</div>
