import { Component, OnInit } from '@angular/core';
import { Employee } from 'src/model/employee';

@Component({
  selector: 'app-user-management',
  templateUrl: './user-management.component.html',
  styleUrls: ['./user-management.component.css']
})
export class UserManagementComponent implements OnInit {

  totalEmployeePerPage: number[] = [5, 6, 7, 8, 9, 10];
  listEmp: any;
  constructor() {
    this.listEmp = this.GenderateListEmp(20);
  }

  ngOnInit() {
  }

  fullname(): any {
    const firstName = ["Trung", "Thanh", "Quoc", "Anh", "Thi Tam", "Thi An", "Quynh Anh", "Van Toan", "Huy"];
    const lastName = ["Tran", "Nguyen", "Ly", "Phan", "Le", "Hoang", "Vu", "Đinh", "Đang", "Duong"];
    const fIndex = Math.floor(Math.random() * firstName.length);
    const lIndex = Math.floor(Math.random() * lastName.length);
    return firstName[fIndex] + lastName[lIndex];
  }


  birthay(): any {
    const dd = Math.floor(Math.random()) + 1;
    const MM= Math.floor(Math.random() * 31) + 1;
    const YY = Math.floor(Math.random() * 15) +  1990;
    return YY + "-" + MM + "-" + dd;
  }

  gender(): any {
    const g = [true, false];
    return g[Math.random()];
  }

  email(): any {

  }

  phone(): any {
    var number = "0";
    for(var i = 0; i < 9; i++){
      number += (Math.floor(Math.random() * 9) +1 ) + "";
    }
    return number;
  }

  address(): any {
    var add = ["HaNoi", "DaNang", "HoChiMinh"];
    return add[Math.floor(Math.random() * add.length)];
  }

  department(): any {
    const de = ["IT", "Marketing", "Sale"];
    return Math.floor(Math.random() * de.length);
  }

  block(): any {
    const g = [true, false];
    return g[Math.random()];
  }

  role(): any{
    return 1;
  }

  GenderateListEmp(total: number): any {
    var list = [];
    for(var i = 0; i < total; i++){
      let emp = {
        ID: i,
        Fullname: this.fullname(),
        Birthday: this.birthay(),
        Gender: this.gender(),
        Email: this.fullname().replace(/\s/g, "") + "@gmail.com",
        Phone: this.phone(),
        Address: this.address(),
        Department: this.department(),
        Block: this.block(),
        RoleID: 1
      }
      list.push(emp);
    }
    return list;
  }
}
<app-header></app-header>
<div class="card">
  <!-- card header -->
  <div class="card-header d-flex justify-content-between">
    <div class="fs-3 fw-bold">Employee Management</div>
    <div>
      <a href="" class="d-flex justify-content-center btn btn-primary">
        <i class="fa-solid fa-user-plus mt-1"></i>
        <div class="mx-2">Add New</div>
      </a>
    </div>
  </div>
  <!-- card body -->
  <div class="card-body">
    <div class="d-flex justify-content-between">
      <!-- Choose total user are displayed in page -->
      <div class="d-flex">
        <div class="mx-2 mt-1">Show</div>
        <select class="form-select align-items-center" id="totalUserOption">
          <option *ngFor="let number of totalEmployeePerPage" >{{number}}</option>
        </select>
        <div class="mt-1">entries</div>
      </div>

      <div class="d-flex">
        <!-- Select user status will be display -->
        <div class="d-flex mx-3">
          <div class="mt-1 mx-2">Status</div>
          <select class="form-select">
            <option value="false">Unblock</option>
            <option value="true">Block</option>
          </select>
        </div>
        <!-- Input name for search -->
        <div class="form-group d-flex">
          <label for="" class="mt-1 mx-2">Name</label>
          <input type="text" class="form-control">
          <button class="btn btn-primary mx-2"><i class="fa-solid fa-magnifying-glass"></i></button>
        </div>
      </div>
    </div>

    <table class="table table-bordered mt-3">
      <thead>
        <tr>
          <th>No.</th>
          <th>Fullname</th>
          <th>Gender</th>
          <th>Email</th>
          <th>Phone</th>
          <th>Action</th>
        </tr>
      </thead>
      <tbody>
        <tr *ngFor="let item of listEmp; let i = index">
          <td>{{i+1}}</td>
          <td>{{item.Fullname}}</td>
          <td>{{item.Gender}}</td>
          <td>{{item.Email}}</td>
          <td>{{item.Phone}}</td>
          <td>
            <div class="d-flex">
              <a href="" class="btn btn-warning">Update</a>
              <div>
                <div *ngIf="item.Block == true" class="btn btn-primary">UnBlock</div>
                <div *ngIf="item.Block == true" class="btn btn-danger">Block</div>
              </div>
            </div>
          </td>
        </tr>
      </tbody>
    </table>
  </div>
</div>
